PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE users(ID integer PRIMARY KEY,name varchar(255),token varchar(255));
CREATE TABLE questions (question_id integer PRIMARY KEY, question varchar(1000) unique,choice1 varchar(255), choice2 varchar(255), choice3 varchar(255), choice4 varchar(255), key integer, marks integer, remarks varchar(255));
CREATE TABLE quiz(quiz_id integer PRIMARY KEY, questions varchar(2000),answerkeys varchar(255));
CREATE TABLE test_instance(id integer PRIMARY KEY, quiz_id integer REFERENCES quiz(quiz_id), user_id integer REFERENCES users(ID), answerkeys varchar(255), score integer);
COMMIT;
